(1081,'年度新增-执行年度新增和完成趋势图',1,'line',0,'','','{\r\n  \"xaxis\":[{\"field\":\"month\",\"name\":\"月份\",\"group\":\"value\"}],\r\n  \"yaxis\":[{\"type\":\"value\",\"field\":\"newExecution\",\"agg\":\"value\",\"name\":\"新增执行数\",\"valOrAgg\":\"value\"},\r\n             {\"type\":\"value\",\"field\":\"closedExecution\",\"agg\":\"value\",\"name\":\"完成执行数\",\"valOrAgg\":\"value\"}\r\n]}','',NULL,'SELECT t1.year, t1.month, IFNULL(t2.execution, 0) AS newExecution, IFNULL(t3.execution, 0) AS closedExecution\r\nFROM (SELECT DISTINCT YEAR(date) AS `year`, MONTH(date) AS `month` FROM zt_action) AS t1 \r\nLEFT JOIN (SELECT YEAR(openedDate) AS `year`, MONTH(openedDate) AS `month`, COUNT(1) AS execution FROM zt_project WHERE deleted = \'0\' AND type IN (\'sprint\', \'stage\', \'kanban\') AND multiple = \'1\' GROUP BY `year`, `month`) AS t2 ON t1.year = t2.year AND t1.month = t2.month \r\nLEFT JOIN (SELECT YEAR(closedDate) AS `year`, MONTH(closedDate) AS `month`, COUNT(1) AS execution FROM zt_project WHERE deleted = \'0\' AND type IN (\'sprint\', \'stage\', \'kanban\') AND status = \'closed\' AND multiple = \'1\' GROUP BY `year`, `month`) AS t3 ON t1.year = t3.year AND t1.month = t3.month\r\nORDER BY `year`, `month`',1,'','0000-00-00 00:00:00','','0000-00-00 00:00:00',0),
(1082,'年度新增-产品发布次数年度趋势图',1,'line',0,'','','{\r\n  \"xaxis\":[{\"field\":\"month\",\"name\":\"月份\",\"group\":\"value\"}],\r\n  \"yaxis\":[{\"type\":\"value\",\"field\":\"release\",\"agg\":\"value\",\"name\":\"发布次数\",\"valOrAgg\":\"value\"}\r\n]}','',NULL,'SELECT \r\n  t1.year, \r\n  t1.month, \r\n  IFNULL(t2.release, 0) AS `release`\r\nFROM \r\n  (\r\n    SELECT \r\n      DISTINCT Year(date) AS `year`, \r\n      MONTH(date) AS `month` \r\n    FROM \r\n      zt_action\r\n  ) AS t1 \r\n  LEFT JOIN (\r\n    SELECT \r\n      YEAR(createdDate) AS `year`, \r\n      MONTH(createdDate) AS `month`, \r\n      COUNT(1) AS `release` \r\n    FROM \r\n      zt_release\r\n    WHERE \r\n      deleted = \'0\'\r\n    GROUP BY \r\n      `year`, \r\n      `month`\r\n  ) AS t2 ON t1.year = t2.year \r\n  AND t1.month = t2.month\r\nORDER BY \r\n  `year`, \r\n  `month`',1,'','0000-00-00 00:00:00','','0000-00-00 00:00:00',0),
(1083,'年度新增-年度投入产出比',1,'line',0,'','','{\r\n  \"xaxis\":[{\"field\":\"month\",\"name\":\"月份\",\"group\":\"value\"}],\r\n  \"yaxis\":[{\"type\":\"value\",\"field\":\"ratio\",\"agg\":\"value\",\"name\":\"投入产出比\",\"valOrAgg\":\"value\"},\r\n{\"type\":\"value\",\"field\":\"story\",\"agg\":\"value\",\"name\":\"需求交付\",\"valOrAgg\":\"value\"},\r\n{\"type\":\"value\",\"field\":\"consumed\",\"agg\":\"value\",\"name\":\"工时消耗\",\"valOrAgg\":\"value\"}\r\n]}','',NULL,'SELECT t1.`year`, t1.`month`, IFNULL(t2.story, 0) AS story, IFNULL(t3.consumed, 0) AS consumed, ROUND(IF(IFNULL(t3.consumed, 0) = 0, 0, IFNULL(t2.story, 0) / IFNULL(t3.consumed, 0)), 2) AS ratio\r\nFROM (SELECT YEAR(`date`) AS \'year\', MONTH(`date`) AS \'month\' FROM zt_action GROUP BY `year`,`month`) AS t1\r\nLEFT JOIN (SELECT ROUND(SUM(estimate)) AS story, YEAR(`closedDate`) AS \'year\', MONTH(`closedDate`) AS \'month\' FROM zt_story WHERE  deleted = \'0\' AND closedReason = \'done\' AND status = \'closed\' GROUP BY `year`,`month`) AS t2 ON t1.`year` = t2.`year` AND t1.`month` = t2.`month`\r\nLEFT JOIN (SELECT ROUND(SUM(consumed)) as consumed, YEAR(`date`) as \'year\', MONTH(`date`) AS \'month\' FROM zt_effort WHERE deleted = \'0\' GROUP BY `year`,`month`) AS t3 ON t1.`year` = t3.`year` AND t1.`month` = t3.`month`',1,'','0000-00-00 00:00:00','','0000-00-00 00:00:00',0),
(1085,'年度排行-项目集-预算投入榜',1,'bar',0,'','','{\"xaxis\":[{\"field\":\"program\",\"name\":\"项目集\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"budget\",\"agg\":\"value\",\"name\":\"预算\",\"valOrAgg\":\"value\"}]}','',NULL,'SELECT \r\n  YEAR(t2.openedDate) AS `year`, \r\n  t1.id,\r\n  t1.name AS program, \r\n  ROUND(\r\n    SUM(\r\n      IFNULL(t2.budget, 0)\r\n    ) / 10000, \r\n    2\r\n  ) AS budget \r\nFROM \r\n  zt_project AS t1 \r\n  LEFT JOIN zt_project AS t2 ON FIND_IN_SET(t1.id, t2.path) \r\n  AND t2.deleted = \'0\' \r\n  AND t2.type = \'project\' \r\nWHERE \r\n  t1.deleted = \'0\' \r\n  AND t1.type = \'program\' \r\n  AND t1.grade = 1 \r\nGROUP BY \r\n  `year`, \r\n  id,\r\n  program \r\nORDER BY \r\n  `year`, \r\n  budget DESC',1,'','0000-00-00 00:00:00','','0000-00-00 00:00:00',0),
